class WidgetToImageConverter {
  static const tickedMarkerIconBase64 =
      "";
  static const darkTickedMarkerIconBase64 =
      "";
  static const orangeMarkerIconBase64 =
      "";

  static const redMarkerIconBase64 =
      "";

  static const yellowMarkerIconBase64 =
      "";

  static BitmapDescriptor get tickedMarkerIcon {
    return BitmapDescriptor.fromBytes(base64Decode(tickedMarkerIconBase64));
  }

  static BitmapDescriptor get orangeMarkerIcon {
    return BitmapDescriptor.fromBytes(base64Decode(orangeMarkerIconBase64));
  }

  static BitmapDescriptor get redMarkerIcon {
    return BitmapDescriptor.fromBytes(base64Decode(redMarkerIconBase64));
  }

  static BitmapDescriptor get yellowMarkerIcon {
    return BitmapDescriptor.fromBytes(base64Decode(yellowMarkerIconBase64));
  }

  static BitmapDescriptor get darkTickedMarkerIcon {
    return BitmapDescriptor.fromBytes(base64Decode(darkTickedMarkerIconBase64));
  }

